def count_tokens(messages):
    """ä¼°ç®—æ¶ˆæ¯æ€»tokenæ•°ï¼ˆç®€å•æŒ‰å­—ç¬¦æ•°/4ï¼‰"""
    return sum(len(m.content) // 4 for m in messages)

def fold_code_blocks(text, max_lines=10):
    """
    å¯¹ markdown ä»£ç å—å†…å®¹åšæŠ˜å ï¼Œåªä¿ç•™å‰åå„ max_lines//2 è¡Œ
    """
    def replacer(match):
        code = match.group(0)
        lines = code.splitlines()
        if (len(lines) > max_lines):
            keep = max_lines // 2
            return '\n'.join(lines[:keep]) + '\n...\n' + '\n'.join(lines[-keep:])
        return code
    # åŒ¹é… ``` å¼€å¤´å’Œç»“å°¾çš„ä»£ç å—
    return re.sub(r"```[\s\S]+?```", replacer, text)

def is_code_message(content):
    """åˆ¤æ–­æ¶ˆæ¯æ˜¯å¦ä¸ºä»£ç å—"""
    # ç®€å•åˆ¤æ–­ï¼šä»¥```å¼€å¤´æˆ–ç»“å°¾ï¼Œæˆ–å†…å®¹å¤§éƒ¨åˆ†ä¸ºä»£ç 
    return content.strip().startswith("```") or content.strip().endswith("```") or len(re.findall(r"```[\s\S]+?```", content)) > 0

